ROOT = os.path.dirname(__file__)
OUTPUT_FILE = os.path.realpath(os.path.join(ROOT, "..", "output", "output.html"))

def main(citation_text_filepath):
  # Read in the unformatted citations.
  with open(citation_text_filepath, 'r') as f:
    txt = f.read()

  # Split the citations by newline chars.
  lines = txt.splitlines(keepends=False)
  
  # Get rid of any potential empty entries due to double spacing.
  idx = 0
  while idx < len(lines):
    if not lines[idx]: # indicates this entry is empty.
      lines.pop(idx)
    idx += 1

  # Get rid of the numbers if they are present in front of the citations.
  for i, line in enumerate(lines):
    parsed_line = line.split()
    potential_enumeration = parsed_line[0][:-1]
    if potential_enumeration.isnumeric():
      # This indicates the first block of text is a number and we should get rid of it.
      lines[i] = ' '.join(parsed_line[1:])
  
  # Insert the PMC link if it is present.
  for i, line in enumerate(lines):
    parsed_line = line.split()
    pmc_link = get_link(parsed_line)
    parsed_line[-1] = pmc_link
    lines[i] = ' '.join(parsed_line)

  # Wrap the citations in enumeration for HTML code.
  html_txt = HTML_HEADER+"<ol>"
  for line in lines:
    html_txt += "\n<li><p class=\"zfr3Q\">"+line+"</p></li>"
  html_txt += "</ol>"

  with open(OUTPUT_FILE, 'w') as f:
    f.write(html_txt)
  
  print (f"Wrote HTML-formatted citations to {OUTPUT_FILE}")

def get_link(parsed_line):
  """Returns the PMC link of the parsed citation

  Parameters
  ----------
  parsed_line : list
      The parsed citation (parsed by whitespace).
  """
  # The PMC link will always be last and is always seven numbers
  link_text = parsed_line[-1]
  
  # Get rid of the period if it's there.
  if link_text[-1] == '.':
    link_text = link_text[:-1]

  if link_text.startswith("PMC"):
    link = "<a href="+PMC_LINK_FORMAT.format(PMC_ID=link_text)+'>'+link_text+"</a>."
  elif link_text.isnumeric():
    link = "<a href="+PM_LINK_FORMAT.format(PM_ID=link_text)+'>'+link_text+"</a>."
  elif link_text == "PMID":
    print (f"No ID found for citation:\n\t{' '.join(parsed_line)}")
    link = link_text
  else:
    print (f"Unknown error occurred for citation:\n\t{' '.join(parsed_line)}")
    link = link_text

  return link


if __name__ == "__main__":
  main(sys.argv[-1])